import axios from 'axios'
import vue from 'vue';
const Vue = new vue();
const qs = require('qs')
const http = {
    async get (url, data) {
      try {
        let res = await axios.get(url, {params: data})
        if(!res.hasOwnProperty('data')){
           res['data'] = ''
        }
        else
        {
          res = res.data
        }
        return new Promise((resolve) => {
          if (res.code === 200) {
            resolve(res)
          } else {
            resolve(res)
          }
        })
      } catch (err) {
        Vue.$message.error(err.msg);
      }
    },
    async post (url, data) {
      try {
        let res = await axios.post(url, qs.stringify(data))
        return new Promise((resolve, reject) => {
          if (res.code === 200) {
            resolve(res)
          } else {
            reject(res)
          }
        })
      } catch (err) {
        Vue.$message.error(err.msg);
        return new Promise((resolve, reject) => {
          resolve(err)
        })
      }
    },
  }
  export { http }
